-- ----------------------------
-- Table structure for dish
-- ----------------------------
DROP TABLE IF EXISTS "public"."dish";
CREATE TABLE "public"."dish" (
  "id" int8 NOT NULL,
  "name" varchar(64) COLLATE "pg_catalog"."default" NOT NULL,
  "category_id" int8 NOT NULL,
  "price" numeric(10,2) NOT NULL,
  "code" varchar(64) COLLATE "pg_catalog"."default" NOT NULL,
  "image" varchar(200) COLLATE "pg_catalog"."default" NOT NULL,
  "description" varchar(400) COLLATE "pg_catalog"."default",
  "status" int4 NOT NULL,
  "sort" int4 NOT NULL,
  "created_time" timestamp(6) NOT NULL,
  "updated_time" timestamp(6) NOT NULL,
  "created_user" int8 NOT NULL,
  "updated_user" int8 NOT NULL,
  "is_deleted" varchar(7) COLLATE "pg_catalog"."default" NOT NULL
)
;
COMMENT ON COLUMN "public"."dish"."id" IS '主鍵';
COMMENT ON COLUMN "public"."dish"."name" IS '菜品名稱';
COMMENT ON COLUMN "public"."dish"."category_id" IS '菜品分類ID';
COMMENT ON COLUMN "public"."dish"."price" IS '菜品價格';
COMMENT ON COLUMN "public"."dish"."code" IS '商品碼';
COMMENT ON COLUMN "public"."dish"."image" IS '圖片';
COMMENT ON COLUMN "public"."dish"."description" IS '描述信息';
COMMENT ON COLUMN "public"."dish"."status" IS '0:停售，1:起售';
COMMENT ON COLUMN "public"."dish"."sort" IS '順序';
COMMENT ON COLUMN "public"."dish"."created_time" IS '創建時間';
COMMENT ON COLUMN "public"."dish"."updated_time" IS '更新時間';
COMMENT ON COLUMN "public"."dish"."created_user" IS '創建人';
COMMENT ON COLUMN "public"."dish"."updated_user" IS '修改者';
COMMENT ON COLUMN "public"."dish"."is_deleted" IS '邏輯刪除字段';
COMMENT ON TABLE "public"."dish" IS '菜品管理';

